        b.add(years, "years");

        var months = a.diff(b, "months");
        b.add(months, "months");

        var days = a.diff(b, "days");

        if (years >=18){
          this.caracData.programa_icbf = "NA";
        }else{
          this.caracData.programa_icbf = "";
        }
        if (years <=8 ){
          this.caracData.estado_civil = 9;
        }else{
          this.caracData.estado_civil = "";
        }                
